mapper.select:
SELECT brd.*, GET_MEMBER_NAME(mem_id) AS mem_name FROM ( SELECT /*+ INDEX_DESC(b pk_board) */ rownum rn , b.* FROM board b WHERE
b.brd_md = 100 AND b.brd_step = 0 AND rownum <= #{pageNum} * #{amount} ) brd
WHERE rn > (#{pageNum} -1) * #{amount}

SELECT 	b.*, GET_MEMBER_NAME(mem_id) AS mem_name
FROM	board b
WHERE	b.brd_id = ${brd_id}

mapper.update:
UPDATE	board
SET		replycnt = replycnt + #{amount}
WHERE	brd_id = #{brd_id}

UPDATE	board
SET		view_cnt = view_cnt + 1
WHERE	brd_id = #{brd_id}
